using System;\nusing System.Data;\nusing System.IO;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string filePath = "path_to_your_file.txt";\n        \n        // Initialize the DataSet and DataTable\n        DataSet ds = new DataSet();\n        DataTable dt = new DataTable();\n\n        // Define columns (adjust names and types based on your file content)\n        dt.Columns.Add("Column1", typeof(string));\n        dt.Columns.Add("Column2", typeof(int));\n        dt.Columns.Add("Column3", typeof(DateTime));\n\n        // Read the file\n        using (StreamReader sr = new StreamReader(filePath))\n        {\n            string line;\n            bool isFirstLine = true;  // Assuming first line is header\n\n            while ((line = sr.ReadLine()) != null)\n            {\n                if (isFirstLine) { isFirstLine = false; continue; } // Skip the header line\n\n                string[] values = line.Split(',');\n\n                // Parse and add row to DataTable\n                dt.Rows.Add(\n                    Convert.ToString(values[0]),\n                    int.Parse(values[1]),  // Adjust parsing based on your needs\n                    DateTime.Parse(values[2])\n                );\n            }\n        }\n\n        ds.Tables.Add(dt);\n        \n        // Now you can use ds for further operations\n    }\n}