public static void AddSomething(string something)\n{\n    // Use connection string from configuration for better security and maintainability\n    string connectionString = "your_connection_string_here";\n    \n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        connection.Open();\n        // Create and execute a command here\n        using (SqlCommand command = new SqlCommand("YourCommand", connection))\n        {\n            // Add parameters, set CommandType, etc.\n            command.ExecuteNonQuery();\n        }\n    } // Connection is closed when it goes out of scope due to the using statement\n}