public class myOtherClass {\n    public string theStringInMyClass { get; set; }\n}\n\npublic class myClass {\n    public List<myOtherClass> anewList = new List<myOtherClass>();\n\n    public void addToList(myOtherClass tmp) {\n        anewList.Add(tmp); // Ensure 'tmp' is a new instance each time\n    }\n}\n\nclass Program {\n    static void Main() {\n        var tmpClass = new myClass();\n        \n        for (int i = 0; i < 100; i++) {\n            var newItem = new myOtherClass { theStringInMyClass = "Item" + i };\n            tmpClass.addToList(newItem);\n        }\n\n        foreach (var tmpC in tmpClass.anewList) {\n            Console.WriteLine(tmpC.theStringInMyClass.ToString());\n        }\n    }\n}