using System;\nusing System.Reflection;\n\npublic class AppPathFinder\n{\n    public static void Main()\n    {\n        // Get the executing assembly's location\n        string appLocation = Assembly.GetExecutingAssembly().Location;\n\n        // Output to debug or log, depending on your application\n        Console.WriteLine("Application is located at: " + appLocation);\n    }\n}