using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        // URL of your PHP script\n        string url = "http://example.com/your_script.php";\n\n        // Parameters to pass to the script\n        var parameters = new FormUrlEncodedContent(new[]\n        {\n            new KeyValuePair<string, string>("param1", "value1"),\n            new KeyValuePair<string, string>("param2", "value2")\n        });\n\n        using (HttpClient client = new HttpClient())\n        {\n            try\n            {\n                // Sending a POST request with the parameters\n                HttpResponseMessage response = await client.PostAsync(url, parameters);\n\n                response.EnsureSuccessStatusCode();\n\n                string responseBody = await response.Content.ReadAsStringAsync();\n                Console.WriteLine(responseBody);\n            }\n            catch (HttpRequestException e)\n            {\n                Console.WriteLine("\nException Caught!");\n                Console.WriteLine("Message :{0} ", e.Message);\n            }\n        }\n    }\n}