public class MyClassExample\n{\n    public static void MyStaticMethod()\n    {\n        Console.WriteLine("Static method called.");\n    }\n\n    public void InstanceMethod()\n    {\n        Console.WriteLine("Instance method called.");\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        // Accessing static method through class name\n        MyClassExample.MyStaticMethod();\n\n        var myInstance = new MyClassExample();\n        \n        // Compilation error if tried to access via instance\n        // myInstance.MyStaticMethod();  // Uncommenting this line will cause an error\n    }\n}