using System;\nusing HtmlAgilityPack;\nusing Ganss.XSS;\n\npublic class RssFeedProcessor\n{\n    public string SanitizeHtml(string htmlContent)\n    {\n        // Create an instance of HtmlSanitizer\n        var sanitizer = new HtmlSanitizer();\n\n        // Allow only certain tags and attributes\n        sanitizer.AllowedTags.Add("br");\n        sanitizer.AllowedTags.Add("a");\n        sanitizer.AllowedAttributes.Add("a", "href");\n\n        // Sanitize the input HTML content\n        string sanitizedContent = sanitizer.Sanitize(htmlContent);\n\n        return sanitizedContent;\n    }\n\n    public void ProcessRssItemDescription(string description)\n    {\n        // Decode any encoded HTML entities before sanitizing\n        string decodedDescription = System.Net.WebUtility.HtmlDecode(description);\n        \n        // Sanitize the description\n        string safeDescription = SanitizeHtml(decodedDescription);\n        \n        // Use or display safeDescription as needed\n        Console.WriteLine(safeDescription);\n    }\n}