<%@ Page Language="C#" AutoEventWireup="true" CodeBehind="YourPage.aspx.cs" Inherits="YourNamespace.YourPage" %>\n<%@ Register Assembly="AjaxControlToolkit" Namespace="AjaxControlToolkit" TagPrefix="ajaxToolkit" %>\n\n<!DOCTYPE html>\n<html xmlns="http://www.w3.org/1999/xhtml">\n<head runat="server">\n    <title>Ajax Example</title>\n</head>\n<body>\n    <form id="form1" runat="server">\n        <asp:ScriptManager ID="ScriptManager1" runat="server"></asp:ScriptManager>\n\n        <ajaxToolkit:UpdatePanel ID="UpdatePanel1" runat="server" UpdateMode="Conditional">\n            <ContentTemplate>\n                <table>\n                    <tr>\n                        <td><asp:TextBox ID="TextBox1" runat="server"></asp:TextBox></td>\n                        <td><asp:TextBox ID="TextBox2" runat="server"></asp:TextBox></td>\n                    </tr>\n                </table>\n            </ContentTemplate>\n        </ajaxToolkit:UpdatePanel>\n\n        <asp:Button ID="Button1" runat="server" Text="Set Values to 1" OnClick="Button1_Click" />\n    </form>\n</body>\n</html>