string filePath = @"C:\path\to\your\file.txt";\nstring streamName = "MyStream";\n\ntry\n{\n    // Read from the alternate data stream\n    string content = AdsOperations.ReadAds(filePath, streamName);\n    Console.WriteLine("Content of ADS: " + content);\n\n    // Write to the alternate data stream\n    byte[] newContent = Encoding.Default.GetBytes("New content for ADS.");\n    AdsOperations.WriteAds(filePath, streamName, newContent);\n}\ncatch (Exception ex)\n{\n    Console.WriteLine("An error occurred: " + ex.Message);\n}