using System;\nusing System.Data.OleDb;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = @"Provider=Microsoft.ACE.OLEDB.12.0;Data Source=C:\path\to\your\db.accdb;";\n        \n        using (OleDbConnection connection = new OleDbConnection(connectionString))\n        {\n            try\n            {\n                connection.Open();\n                Console.WriteLine("Connection successful.");\n\n                string tableCreationQuery = \n                    "CREATE TABLE Users (" +\n                        "ID INTEGER PRIMARY KEY," +\n                        "Name TEXT NOT NULL," +\n                        "Email TEXT NOT NULL)";\n\n                using (OleDbCommand command = new OleDbCommand(tableCreationQuery, connection))\n                {\n                    command.ExecuteNonQuery();\n                    Console.WriteLine("Table created.");\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Error: " + ex.Message);\n            }\n        }\n    }\n}