// Define a custom class with only the properties you want to display\npublic class AirportsDisplay\n{\n    public string Name { get; set; }\n    public string City { get; set; }\n}\n\n// Usage\nList<Airports> globalDisplayAirports = GetGlobalDisplayAirports(); // Assume this method fetches your list\n\n// Create a new list with only the properties you want to display\nList<AirportsDisplay> filteredAirports = globalDisplayAirports.Select(airport => \n    new AirportsDisplay \n    {  \n        Name = airport.Name, \n        City = airport.City \n    }).ToList();\n\nDataGridViewAirport.DataSource = filteredAirports;