// Assuming 'objects' is your IQueryable<MyObject> from your LINQ to SQL setup.\nvar sortedObjects = objects.Select(o => new {\n    OriginalObject = o,\n    SortValue = (theSortPropertyType == "int")\n        ? Convert.ToInt32(o.MyObjectProperties.Find(s => s.Name == theSortProperty).Value)\n        : o.MyObjectProperties.Find(s => s.Name == theSortProperty).Value\n}).OrderBy(x => x.SortValue is int ? (int)x.SortValue : (string)x.SortValue)\n  .Select(x => x.OriginalObject);\n\n// Use sortedObjects to execute the query.