public partial class MainForm : Form\n{\n    // Declare associateForm as a member variable\n    private associateForm associateForm;\n\n    public MainForm()\n    {\n        InitializeComponent();\n        \n        // Instantiate associateForm in the constructor or another initialization method\n        associateForm = new associateForm();\n    }\n\n    private void notifyIcon1_MouseDoubleClick(object sender, MouseEventArgs e)\n    {\n        if (e.Button == MouseButtons.Left)\n        {\n            if (associateForm.WindowState == FormWindowState.Normal && associateForm.Visible)\n            {\n                associateForm.WindowState = FormWindowState.Minimized;\n            }\n            else\n            {\n                associateForm.WindowState = FormWindowState.Normal;\n            }\n\n            associateForm.Show();\n            associateForm.Focus();\n            // Bring to front of other windows\n            associateForm.Activate();\n        }\n    }\n}