using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic DataTable[] ExecuteBatchQuery(string connectionString)\n{\n    var dataTableArray = new DataTable[3];\n\n    using (var connection = new SqlConnection(connectionString))\n    {\n        connection.Open();\n\n        // Create a command with multiple SQL statements.\n        using (var command = new SqlCommand())\n        {\n            command.Connection = connection;\n            command.CommandText =\n                "SELECT * FROM Customers; " +\n                "SELECT * FROM Products; " +\n                "SELECT * FROM Orders;";\n\n            // Create a DataAdapter to fill DataTables\n            var adapter = new SqlDataAdapter(command);\n            var dataSet = new DataSet();\n\n            // Fill the DataSet with multiple tables\n            adapter.Fill(dataSet);\n\n            // Extract each table from the DataSet\n            dataTableArray[0] = dataSet.Tables[0];\n            dataTableArray[1] = dataSet.Tables[1];\n            dataTableArray[2] = dataSet.Tables[2];\n        }\n\n        connection.Close();\n    }\n\n    return dataTableArray;\n}