using System;\nusing System.Management;\n\npublic class TerminalsServicesPropertiesReader\n{\n    public static void ReadProperties(string ldapPath)\n    {\n        try\n        {\n            ManagementObject user = new ManagementObject(new ManagementPath(ldapPath));\n            \n            // Ensure to connect to the Terminal Services specific properties\n            user.Get();\n\n            string profilePath = (string)user.Properties["TerminalServicesProfilePath"].Value;\n            string homeDirectory = (string)user.Properties["TerminalServicesHomeDirectory"].Value;\n            string homeDrive = (string)user.Properties["TerminalServicesHomeDrive"].Value;\n\n            Console.WriteLine("Terminal Services Profile Path: " + profilePath);\n            Console.WriteLine("Terminal Services Home Directory: " + homeDirectory);\n            Console.WriteLine("Terminal Services Home Drive: " + homeDrive);\n        }\n        catch (ManagementException ex)\n        {\n            Console.WriteLine("An error occurred while querying for management data: " + ex.Message);\n        }\n    }\n\n    public static void Main()\n    {\n        string ldapPath = "WinNT://YourDomain/YourUserName";\n        // or use LDAP path like: "LDAP://CN=YourUserName,CN=Users,DC=yourdomain,DC=com"\n\n        ReadProperties(ldapPath);\n    }\n}