using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        Stopwatch stopwatch = new Stopwatch();\n        stopwatch.Start();\n\n        long sum = 0;\n        for (long i = 1; i < Int64.MaxValue / 2; i++)\n        {\n            sum += i;\n        }\n\n        stopwatch.Stop();\n        Console.WriteLine("C# Elapsed Time: " + stopwatch.Elapsed);\n    }\n}