using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class QueryStringSecurity\n{\n    private const string _secretKey = "your_secret_key_here"; // Keep this secure!\n\n    public static string GenerateHmacSha256(params (string key, string value)[] pairs)\n    {\n        var queryString = string.Join("&", pairs.Select(p => $"{p.key}={p.value}"));\n        using (var hmacsha256 = new HMACSHA256(Encoding.UTF8.GetBytes(_secretKey)))\n        {\n            var hashBytes = hmacsha256.ComputeHash(Encoding.UTF8.GetBytes(queryString));\n            return BitConverter.ToString(hashBytes).Replace("-", "").ToLower();\n        }\n    }\n\n    public static bool ValidateHmacSha256(string providedHash, params (string key, string value)[] pairs)\n    {\n        var computedHash = GenerateHmacSha256(pairs);\n        return generatedHash == providedHash;\n    }\n}