using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        string queryString = "SELECT * FROM Win32_PerfRawData_Tcpip_IPv4Interface";\n        ManagementObjectSearcher searcher = new ManagementObjectSearcher(queryString);\n        \n        foreach (ManagementObject networkInterface in searcher.Get())\n        {\n            string name = networkInterface["Name"] as string;\n            ulong bytesSent = (ulong)networkInterface["BytesSent"];\n            ulong bytesReceived = (ulong)networkInterface["BytesReceived"];\n\n            Console.WriteLine("Interface: {0}", name);\n            Console.WriteLine("  Bytes Sent:    {1}", bytesSent);\n            Console.WriteLine("  Bytes Received: {0}", bytesReceived);\n        }\n    }\n}