private void SendEmail(string toAddress)\n{\n    try\n    {\n        MailMessage message = new MailMessage(welcomeMessageFrom, toAddress, welcomeMessageSubject, welcomeMessageBody);\n        \n        SmtpClient emailClient = new SmtpClient("mail.sortuv.com", 587);\n        \n        // Use the fully qualified username and ensure correct credentials\n        System.Net.NetworkCredential SMTPUserInfo = new System.Net.NetworkCredential("username@domain.com", password);\n        emailClient.UseDefaultCredentials = false;\n        emailClient.Credentials = SMTPUserInfo;\n        emailClient.EnableSsl = true;  // Enable SSL if required by your SMTP server\n        \n        emailClient.Send(message);\n    }\n    catch (Exception ex)\n    {\n        // Log or handle exception appropriately\n        Console.WriteLine("Email sending failed: " + ex.Message);\n    }\n}