public partial class YourPage : System.Web.UI.Page\n{\n    private bool _isRequestValidationFailed = false;\n\n    protected override void OnInit(EventArgs e)\n    {\n        try\n        {\n            base.OnInit(e);\n        }\n        catch (HttpRequestValidationException ex)\n        {\n            _isRequestValidationFailed = true;\n            // Clear the error so that it doesn't bubble up\n            Server.ClearError();\n        }\n    }\n\n    protected override void OnLoad(EventArgs e)\n    {\n        base.OnLoad(e);\n\n        if (_isRequestValidationFailed)\n        {\n            // Display a custom error message instead of a default one\n            lblErrorMessage.Text = "Invalid input detected. Please check your entries and try again.";\n        }\n    }\n}