using Microsoft.EntityFrameworkCore;\n\n// Define your data model class\npublic class Product\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public decimal Price { get; set; }\n}\n\n// Define the DBContext, which represents a session with the database\npublic class AppDbContext : DbContext\n{\n    public DbSet<Product> Products { get; set; }\n\n    protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)\n    {\n        optionsBuilder.UseSqlServer("Server=(localdb)\\mssqllocaldb;Database=YourDbName;Trusted_Connection=True;");\n    }\n}\n\n// Using the DbContext to interact with the database\npublic class ProductService\n{\n    private readonly AppDbContext _context;\n\n    public ProductService(AppDbContext context)\n    {\n        _context = context;\n    }\n\n    public async Task<IEnumerable<Product>> GetProductsAsync()\n    {\n        return await _context.Products.ToListAsync();\n    }\n}