public interface IVehicle\n{\n    void Drive();\n}\n\npublic class Car : IVehicle\n{\n    public void Drive()\n    {\n        Console.WriteLine("Car is driving.");\n    }\n}\n\npublic class VehicleDecorator : IVehicle\n{\n    private readonly IVehicle _vehicle;\n    private int _methodCallCount;\n\n    public VehicleDecorator(IVehicle vehicle)\n    {\n        _vehicle = vehicle;\n        _methodCallCount = 0;\n    }\n\n    public void Drive()\n    {\n        _methodCallCount++;\n        Console.WriteLine($"Drive method called {_methodCallCount} times.");\n        _vehicle.Drive();\n    }\n}