using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string path = @"\\server\share\path";\n        \n        if (IsUncPath(path))\n        {\n            Console.WriteLine("This is an UNC path.");\n            // Handle UNC path, DriveInfo doesn't apply here.\n        }\n        else if (DriveExists(path))\n        {\n            Console.WriteLine("This is a local path with a valid drive letter.");\n            var driveInfo = new DriveInfo(Path.GetPathRoot(path));\n            // Use driveInfo object as needed\n        }\n        else\n        {\n            Console.WriteLine("This is either an invalid path or something other than a local drive or UNC path.");\n        }\n    }\n\n    static bool IsUncPath(string path)\n    {\n        return Path.IsPathFullyQualified(path) && path.StartsWith(@"\\", StringComparison.Ordinal);\n    }\n\n    static bool DriveExists(string path)\n    {\n        try\n        {\n            var root = Path.GetPathRoot(path);\n            return !string.IsNullOrEmpty(root) && DriveInfo.GetDrives().Any(d => d.Name == root);\n        }\n        catch (Exception)\n        {\n            // Handle exceptions as needed, e.g., invalid drive format.\n            return false;\n        }\n    }\n}