private bool DoesObjectExist(DataObjectType type, int id)\n{\n    // Implement logic here to check if an object of 'type' with ID 'id' exists\n    switch (type)\n    {\n        case DataObjectType.Customer:\n            return dbContext.Customers.Any(c => c.Id == id);\n        case DataObjectType.SalesLead:\n            return dbContext.SalesLeads.Any(s => s.Id == id);\n        // Add cases as needed\n        default:\n            return false;\n    }\n}\n\n[AcceptVerbs(HttpVerbs.Get)]\npublic ActionResult New(DataObjectType type, int id)\n{\n    if (!DoesObjectExist(type, id))\n    {\n        return HttpNotFound();\n    }\n\n    Note note = new Note { Type = type, Source = id };\n    return View(note);\n}\n\n[AcceptVerbs(HttpVerbs.Post)]\npublic ActionResult Create(Note note)\n{\n    if (!DoesObjectExist(note.Type, note.Source))\n    {\n        ModelState.AddModelError("", "The object does not exist.");\n        return View("New", note);\n    }\n\n    // This is just an example\n    note.Persist();\n    return RedirectToAction("Index"); // or other appropriate action\n}