using System;\n\nclass Program\n{\n    static void Main()\n    {\n        int unixTimestamp = 1236772829; // Example timestamp\n        DateTime utcDateTime = DateTimeOffset.FromUnixTimeSeconds(unixTimestamp).UtcDateTime;\n        DateTime localDateTime = utcDateTime.ToLocalTime();\n\n        Console.WriteLine("UTC DateTime: " + utcDateTime);\n        Console.WriteLine("Local DateTime: " + localDateTime);\n    }\n}