using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        string response = await CallSimMetricsApi("example1", "example2");\n        Console.WriteLine(response);\n    }\n\n    static async Task<string> CallSimMetricsApi(string str1, string str2)\n    {\n        using (HttpClient client = new HttpClient())\n        {\n            // Example URL with parameters\n            string url = $"http://localhost:8080/simmetrics?str1={Uri.EscapeDataString(str1)}&str2={Uri.EscapeDataString(str2)}";\n            \n            HttpResponseMessage responseMessage = await client.GetAsync(url);\n            if (responseMessage.IsSuccessStatusCode)\n            {\n                return await responseMessage.Content.ReadAsStringAsync();\n            }\n            else\n            {\n                throw new Exception($"Error: {responseMessage.StatusCode}");\n            }\n        }\n    }\n}