using System;\nusing System.Data.SqlClient;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your sql server connection string";\n        XDocument xmlData = XDocument.Load("path to your xml file");\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            foreach (var item in xmlData.Descendants("Item"))\n            {\n                var value1 = item.Element("Value1")?.Value;\n                var value2 = item.Element("Value2")?.Value;\n\n                // Use parameterized queries to prevent SQL injection\n                using (SqlCommand command = new SqlCommand(\n                    "INSERT INTO YourTable (Column1, Column2) VALUES (@Value1, @Value2)", connection))\n                {\n                    command.Parameters.AddWithValue("@Value1", value1);\n                    command.Parameters.AddWithValue("@Value2", value2);\n\n                    command.ExecuteNonQuery();\n                }\n            }\n        }\n    }\n}