using System;\nusing System.Configuration;\nusing System.ServiceModel.Description;\n\npublic class StartupHelper\n{\n    // Method to get endpoint address from configuration\n    public static string GetEndpointAddress(string serviceName)\n    {\n        var serviceModelSection = ConfigurationManager.GetSection("system.serviceModel") as System.ServiceModel.Configuration.ServicesSection;\n\n        if (serviceModelSection != null && serviceModelSection.Services.Count > 0)\n        {\n            foreach (ServiceElement service in serviceModelSection.Services)\n            {\n                if (string.Equals(service.Name, serviceName))\n                {\n                    foreach (ServiceEndpointElement endpoint in service.Endpoints)\n                    {\n                        return endpoint.Address;\n                    }\n                }\n            }\n        }\n\n        throw new ArgumentException("Service not found or no endpoints configured.", nameof(serviceName));\n    }\n}\n\n// Usage example during OnStart event of a web app\npublic class WebApp : System.Web.HttpApplication\n{\n    protected void Application_Start(object sender, EventArgs e)\n    {\n        string endpointUrl = StartupHelper.GetEndpointAddress("Sc.Neo.Bus.Server.MessageProxy");\n        Console.WriteLine($"The endpoint URL is: {endpointUrl}");\n    }\n}