using System;\n   using System.Web;\n\n   public class UrlRewriteHandler : IHttpHandler {\n       public void ProcessRequest(HttpContext context) {\n           string path = context.Request.Path.ToLower();\n           if (path.StartsWith("/something/")) {\n               string id = path.Substring(path.LastIndexOf('/') + 1);\n               string redirectUrl = $"http://someotherserver/applications/something/modules/default.aspx?id={id}";\n               context.Response.StatusCode = 302;\n               context.Response.StatusDescription = "Moved Temporarily";\n               context.Response.RedirectLocation = redirectUrl;\n           }\n       }\n\n       public bool IsReusable { get { return false; } }\n   }