using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic void CreateRoundedRectangle(PictureBox pictureBox, int width, int height, int radius)\n{\n    // Create a bitmap with the specified dimensions.\n    using (Bitmap bmp = new Bitmap(width, height))\n    {\n        // Initialize graphics from the defined bitmap.\n        using (Graphics g = Graphics.FromImage(bmp))\n        {\n            // Set the smoothing mode to anti-aliasing for smooth corners.\n            g.SmoothingMode = System.Drawing.Drawing2D.SmoothingMode.AntiAlias;\n\n            // Define a rectangle with rounded corners.\n            RectangleF rect = new RectangleF(0, 0, width - 1, height - 1);\n            GraphicsPath path = new GraphicsPath();\n            path.AddArc(rect.X, rect.Y, radius * 2, radius * 2, 180, 90); // Top-left corner.\n            path.AddArc(rect.Width - radius * 2, rect.Y, radius * 2, radius * 2, 270, 90); // Top-right corner.\n            path.AddArc(rect.Width - radius * 2, rect.Height - radius * 2, radius * 2, radius * 2, 0, 90); // Bottom-right corner.\n            path.AddArc(rect.X, rect.Height - radius * 2, radius * 2, radius * 2, 90, 90); // Bottom-left corner.\n            path.CloseFigure();\n\n            // Fill the rounded rectangle with a solid color, e.g., Blue.\n            using (Brush brush = new SolidBrush(Color.Blue))\n            {\n                g.FillPath(brush, path);\n            }\n        }\n\n        // Assign the bitmap to the PictureBox control.\n        pictureBox.Image = bmp;\n    }\n\n    // Make sure the PictureBox is visible and set its size accordingly.\n    pictureBox.Visible = true;\n    pictureBox.Width = width;\n    pictureBox.Height = height;\n}