using System;\nusing HtmlAgilityPack;\n\nclass Program\n{\n    static void Main()\n    {\n        string html = "<p>This is a <strong>sample</strong> paragraph with <em>important</em> information.</p>";\n        \n        // Load HTML into HtmlDocument\n        var doc = new HtmlDocument();\n        doc.LoadHtml(html);\n        \n        // Get all strong tags\n        var strongNodes = doc.DocumentNode.SelectNodes("//strong");\n        if (strongNodes != null)\n        {\n            foreach (var node in strongNodes)\n            {\n                Console.WriteLine("Text between <strong> and </strong>: " + node.InnerText);\n            }\n        }\n\n        // Get all em tags\n        var emNodes = doc.DocumentNode.SelectNodes("//em");\n        if (emNodes != null)\n        {\n            foreach (var node in emNodes)\n            {\n                Console.WriteLine("Text between <em> and </em>: " + node.InnerText);\n            }\n        }\n    }\n}