using System;\nusing System.Reflection;\nusing System.Windows.Forms;\n\nclass Program\n{\n    [STAThread] // Important for Windows Forms\n    static void Main()\n    {\n        // Load the assembly and get the type\n        Assembly assembly = Assembly.LoadFile("C:\\test.dll");\n        Type type = assembly.GetType("test.dllTest");\n\n        if (type != null)\n        {\n            // Create an instance of the form\n            object instance = Activator.CreateInstance(type);\n\n            // Show the form\n            MethodInfo showMethod = type.GetMethod("Show");\n            if (showMethod != null)\n            {\n                showMethod.Invoke(instance, null);\n            }\n            else\n            {\n                MethodInfo showDialogMethod = type.GetMethod("ShowDialog");\n                if (showDialogMethod != null)\n                {\n                    showDialogMethod.Invoke(instance, null);\n                }\n            }\n\n            // Run the message loop for the Windows Forms application\n            Application.Run();\n        }\n        else\n        {\n            Console.WriteLine("Type not found in assembly.");\n        }\n    }\n}