public class PageInfo\n{\n    public bool IsValidated { get; set; }\n    public float Resolution { get; set; }\n    public string ColorMode { get; set; } // "Color" or "BW"\n}\n\npublic class DocumentInfo\n{\n    public Dictionary<int, Collection<Rectangle>> Coordinates { get; private set; }\n    public Dictionary<int, PageInfo> PageAttributes { get; private set; }\n\n    public DocumentInfo()\n    {\n        Coordinates = new Dictionary<int, Collection<Rectangle>>();\n        PageAttributes = new Dictionary<int, PageInfo>();\n    }\n}\n\npublic DocumentInfo GetDocumentInfo(int DocumentId)\n{\n    // Simulate data fetching\n    var documentInfo = new DocumentInfo();\n\n    // Example of populating the Coordinates and PageAttributes\n    for (int i = 0; i < 5; i++)\n    {\n        documentInfo.Coordinates[i] = new Collection<Rectangle> { /* populate rectangles */ };\n        documentInfo.PageAttributes[i] = new PageInfo \n        { \n            IsValidated = true, \n            Resolution = 300.0f, \n            ColorMode = "Color" \n        };\n    }\n\n    return documentInfo;\n}