// Assuming baseFilePath is the root directory where your temp files are stored\nstring baseFilePath = (string)System.Web.HttpContext.Current.Application["baseFilePath"];\n\nforeach (string item in TempFilesList)\n{\n    // Construct the full file path\n    string filePath = Path.Combine(baseFilePath, "temp", item);\n\n    try\n    {\n        // Check if the file exists before attempting to delete\n        if (File.Exists(filePath))\n        {\n            File.Delete(filePath);\n        }\n        else\n        {\n            Console.WriteLine($"The file {filePath} does not exist.");\n        }\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine($"Error deleting file: {ex.Message}");\n    }\n}