[TestClass]\npublic class UserBusinessTests\n{\n    // Setup is called before every test\n    [TestInitialize()]\n    public void Setup()\n    {\n        // Initialize database with initial state, truncate tables, etc.\n        DatabaseHelper.TruncateUserTable();\n        DatabaseHelper.InsertInitialUsers();\n    }\n\n    // Teardown is called after every test\n    [TestCleanup()]\n    public void Cleanup()\n    {\n        // Clean up any test data added to the database\n        DatabaseHelper.TruncateUserTable();\n    }\n\n    [TestMethod]\n    public void TestAddUser()\n    {\n        var userService = new UserService();\n        var user = newUser { UserId = 1, Name = "Test User" };\n\n        userService.AddUser(user);\n\n        // Assert that user is added correctly\n        var addedUser = DatabaseHelper.GetUserById(1);\n        Assert.IsNotNull(addedUser);\n        Assert.AreEqual(user.Name, addedUser.Name);\n    }\n\n    [TestMethod]\n    public void TestDeleteUser()\n    {\n        var userService = new UserService();\n        var userId = 2; // assume this user is added during setup\n\n        userService.DeleteUser(userId);\n\n        // Assert that the user no longer exists\n        var deletedUser = DatabaseHelper.GetUserById(userId);\n        Assert.IsNull(deletedUser);\n    }\n}