using System;\nusing System.IO;\n\nclass QuizGame\n{\n    static void Main()\n    {\n        string filePath = "quiz.txt";\n        \n        try\n        {\n            using (StreamReader reader = new StreamReader(filePath))\n            {\n                string line;\n                while ((line = reader.ReadLine()) != null)\n                {\n                    string[] parts = line.Split(',');\n                    if (parts.Length != 6) continue; // Skip malformed lines\n\n                    Console.WriteLine(parts[0]); // Question\n                    for (int i = 1; i <= 4; i++)\n                    {\n                        Console.WriteLine($"{i}. {parts[i]}");\n                    }\n\n                    string userAnswer = Console.ReadLine();\n                    int correctIndex = int.Parse(parts[5]) - 1;\n\n                    if (userAnswer == (correctIndex + 1).ToString())\n                    {\n                        Console.WriteLine("Correct!");\n                    }\n                    else\n                    {\n                        Console.WriteLine($"Incorrect! The correct answer was {parts[correctIndex + 1]}");\n                    }\n\n                    Console.WriteLine(); // Blank line between questions\n                }\n            }\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine($"An error occurred: {e.Message}");\n        }\n    }\n}