using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class InvoiceGenerator\n{\n    private HashSet<string> _usedNumbers = new HashSet<string>();\n\n    public List<string> GenerateUniqueInvoiceNumbers(int count, int maxLength = 12)\n    {\n        var invoiceNumbers = new List<string>();\n        while (invoiceNumbers.Count < count)\n        {\n            // Generate a random alphanumeric string\n            string number = GenerateRandomString(maxLength);\n            if (_usedNumbers.Add(number)) // Only add to list and set if it's unique\n            {\n                invoiceNumbers.Add(number);\n            }\n        }\n\n        return invoiceNumbers;\n    }\n\n    private string GenerateRandomString(int length)\n    {\n        const string chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";\n        var random = new Random();\n        return new string(Enumerable.Repeat(chars, length)\n            .Select(s => s[random.Next(s.Length)]).ToArray());\n    }\n}