public abstract class ApplicationController<T> : Controller where T : User\n{\n    protected virtual new ViewResult View(string viewName, object model)\n    {\n        var user = GetUser(); // Implement your logic to get the logged-in user here\n\n        if (model is ApplicationViewModel<T> applicationModel)\n        {\n            applicationModel.LoggedInUser = user;\n        }\n\n        return base.View(viewName, model);\n    }\n}