using System.Drawing;\nusing System.Windows;\nusing System.Windows.Controls;\nusing System.Windows.Media;\n\nSystem.Drawing.Font font = FontFromLegacyCode();\n\nLabel label = new Label();\nlabel.Content = "Sample Text"; // Set the content of the label\n\n// Basic font properties setup\nlabel.FontFamily = new FontFamily(font.Name);\nlabel.FontWeight = font.Bold ? FontWeights.Bold : FontWeights.Regular;\nlabel.FontStyle = font.Italic ? FontStyles.Italic : FontStyles.Normal;\nlabel.FontSize = font.Size;\n\n// Setting underline and strikeout using TextDecorations\nif (font.Underline)\n{\n    label.TextDecorations = new System.Windows.TextDecorationCollection(new[] { TextDecorations.Underline[0] });\n}\n\nif (font.Strikeout)\n{\n    label.TextDecorations.Add(TextDecorations.Strikethrough[0]);\n}