protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Initial state setup if needed\n        SetDivVisibility("myDynamicDiv1", true);\n        SetDivVisibility("myDynamicDiv2", false);\n\n        // Bind data if necessary\n        DataBind();\n    }\n}\n\nprotected void SomeButton_Click(object sender, EventArgs e)\n{\n    // Example of changing visibility based on some action\n    bool isVisible = GetDivVisibility("myDynamicDiv1");\n    SetDivVisibility("myDynamicDiv1", !isVisible);\n    \n    // Refresh the view state to reflect the changes\n    DataBind();\n}\n\nprivate void SetDivVisibility(string divId, bool visible)\n{\n    ViewState[divId] = visible ? "display:block;" : "display:none;";\n}\n\nprivate bool GetDivVisibility(string divId)\n{\n    string style = ViewState[divId] as string;\n    return !string.IsNullOrEmpty(style) && style.Contains("block");\n}