using System;\nusing System.Messaging;\n\nclass Program\n{\n    static void Main()\n    {\n        string queuePath = @".\Private$\MyQueue";\n\n        // Create the queue if it does not exist\n        if (!MessageQueue.Exists(queuePath))\n        {\n            MessageQueue.Create(queuePath, true);\n        }\n\n        using (MessageQueue myQueue = new MessageQueue(queuePath))\n        {\n            // Send a message to the queue\n            myQueue.Send("Your message data here");\n\n            // Optionally receive a message from the queue\n            var receivedMsg = myQueue.Receive();\n            Console.WriteLine(receivedMsg.Body.ToString());\n        }\n    }\n}