using System;\nusing System.Linq;\nusing System.Web.Security;\n\npublic static bool IsPasswordValid(string password)\n{\n    MembershipProvider provider = Membership.Provider;\n    string errorMessage;\n\n    // Check if the new password passes all validation requirements.\n    return provider.ValidateUser(_usernameTextBox.Text, _passNewTextBox.Text) || \n           (provider.GetPasswordStrength(password, out errorMessage) >= 0 && errorMessage == null);\n}