using System;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static void Main()\n    {\n        int maxIterations = 1000; // Example number of iterations\n\n        Parallel.For(0, maxIterations, i =>\n        {\n            // Perform your calculation here\n            long result = SomeHeavyCalculation(i);\n            \n            // Optionally handle results if needed\n            Console.WriteLine(result); // Note: Console.Write can be a bottleneck\n        });\n\n        Console.WriteLine("All calculations completed.");\n    }\n\n    static long SomeHeavyCalculation(long i)\n    {\n        // Example heavy calculation\n        long sum = 0;\n        for (long j = 0; j <= i * 100L; j++)\n        {\n            sum += j;\n        }\n        return sum;\n    }\n}