// Example usage:\nstring original = "The quick brown fox jumps over the lazy dog";\n\nbyte[] Key = new byte[32]; // AES-256 requires a 256-bit key\nusing (var rng = RandomNumberGenerator.Create())\n    rng.GetBytes(Key);\n\nbyte[] IV = new byte[16]; // AES block size is 128 bits\nrng.GetBytes(IV);\n\nstring encryptedCookieData = EncryptStringToAes(original, Key, IV);\nstring decryptedCookieData = DecryptStringFromAes(encryptedCookieData, Key, IV);\n\nConsole.WriteLine("Original:   {0}", original);\nConsole.WriteLine("Encrypted:  {0}", encryptedCookieData);\nConsole.WriteLine("Decrypted:  {0}", decryptedCookieData);