using System;\nusing System.Collections.Generic;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString1 = "Data Source=YourServer;Initial Catalog=Database1;Integrated Security=True";\n        string connectionString2 = "Data Source=YourServer;Initial Catalog=Database2;Integrated Security=True";\n\n        List<string> tables = new List<string> { "Table1", "Table2" }; // Add all your table names here\n\n        foreach (var table in tables)\n        {\n            var hashSet1 = GetHashes(connectionString1, table);\n            var hashSet2 = GetHashes(connectionString2, table);\n\n            if (!hashSet1.SetEquals(hashSet2))\n            {\n                Console.WriteLine($"Data mismatch found in table: {table}");\n            }\n            else\n            {\n                Console.WriteLine($"Table {table} is consistent.");\n            }\n        }\n    }\n\n    static HashSet<int> GetHashes(string connectionString, string tableName)\n    {\n        HashSet<int> hashes = new HashSet<int>();\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            using (SqlCommand command = new SqlCommand($"SELECT CHECKSUM(*) FROM {tableName}", connection))\n            {\n                using (SqlDataReader reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        hashes.Add(reader.GetInt32(0));\n                    }\n                }\n            }\n        }\n        return hashes;\n    }\n}