using System;\nusing System.Diagnostics;\n\nclass SvgToPdfConverter\n{\n    static void Main(string[] args)\n    {\n        string inkscapePath = @"C:\Program Files\Inkscape\inkscape.exe";\n        string svgFilePath = @"C:\path\to\your\file.svg";\n        string pdfOutputPath = @"C:\path\to\output.pdf";\n\n        ProcessStartInfo startInfo = new ProcessStartInfo\n        {\n            FileName = inkscapePath,\n            Arguments = $"\"{svgFilePath}\" -o \"{pdfOutputPath}\" --export-pdf-version=1.5",\n            RedirectStandardError = true,\n            RedirectStandardOutput = true,\n            UseShellExecute = false,\n            CreateNoWindow = true\n        };\n\n        Process process = new Process { StartInfo = startInfo };\n        \n        try\n        {\n            process.Start();\n            string output = process.StandardOutput.ReadToEnd();\n            string error = process.StandardError.ReadToEnd();\n            process.WaitForExit();\n\n            if (!string.IsNullOrEmpty(error))\n            {\n                Console.WriteLine("Inkscape Error: " + error);\n            }\n            else\n            {\n                Console.WriteLine("Conversion successful!");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Failed to convert SVG to PDF: " + ex.Message);\n        }\n    }\n}