using System;\nusing System.Collections.Specialized;\nusing System.Windows.Forms;\n\nnamespace AutoCompleteExample\n{\n    public partial class Form1 : Form\n    {\n        private StringCollection recentEntries = new StringCollection();\n\n        public Form1()\n        {\n            InitializeComponent();\n            InitializeAutoComplete();\n        }\n\n        private void InitializeAutoComplete()\n        {\n            // Load existing entries (e.g., from file or database)\n            // For demonstration, we'll just add some sample entries.\n            recentEntries.Add("Document1.txt");\n            recentEntries.Add("Report2.pdf");\n\n            // Set the AutoCompleteCustomSource\n            textBox1.AutoCompleteCustomSource = recentEntries;\n\n            // Enable auto-complete mode\n            textBox1.AutoCompleteMode = AutoCompleteMode.SuggestAppend;\n            textBox1.AutoCompleteSource = AutoCompleteSource.CustomSource;\n        }\n\n        private void textBox1_TextChanged(object sender, EventArgs e)\n        {\n            // Optionally update/modify the list based on new inputs\n            if (!string.IsNullOrEmpty(textBox1.Text) && !recentEntries.Contains(textBox1.Text))\n            {\n                recentEntries.Add(textBox1.Text);\n                textBox1.AutoCompleteCustomSource = recentEntries;\n            }\n        }\n    }\n}