using System.IO;\nusing System.Web;\n\npublic void SendReportToClient(string reportContent, string fileName)\n{\n    byte[] bytes = System.Text.Encoding.UTF8.GetBytes(reportContent);\n    \n    // Clear the response and add necessary headers\n    HttpContext.Current.Response.Clear();\n    HttpContext.Current.Response.ContentType = "text/plain"; // Set to appropriate MIME type\n    HttpContext.Current.Response.AddHeader("content-disposition", "attachment; filename=" + fileName);\n    \n    // Write bytes directly to the output stream\n    HttpContext.Current.Response.OutputStream.Write(bytes, 0, bytes.Length);\n    HttpContext.Current.Response.End();\n}