public partial class MyForm : Form\n{\n    private bool _isDirty;\n    private Dictionary<Control, object> _initialValues;\n\n    public MyForm()\n    {\n        InitializeComponent();\n        InitializeFormData();\n        TrackInitialValues();\n    }\n\n    private void InitializeFormData()\n    {\n        // Example of data binding, replace with your actual binding logic\n        textBox1.DataBindings.Add("Text", myDataSource, "PropertyName");\n        comboBox1.DataBindings.Add("SelectedItem", anotherDataSource, "AnotherProperty");\n    }\n\n    private void TrackInitialValues()\n    {\n        _isDirty = false;\n        _initialValues = new Dictionary<Control, object>();\n        foreach (var control in this.Controls)\n        {\n            if (control is TextBox textBox)\n                _initialValues[textBox] = textBox.Text;\n            else if (control is ComboBox comboBox && comboBox.SelectedItem != null)\n                _initialValues[comboBox] = comboBox.SelectedItem;\n            // Add more controls as necessary\n        }\n    }\n\n    private void Control_TextChangedOrSelectedIndexChanged(object sender, EventArgs e)\n    {\n        var control = sender as Control;\n        if (_initialValues.TryGetValue(control, out object initialValue))\n        {\n            if ((control is TextBox textBox && textBox.Text != (string)initialValue) ||\n                (control is ComboBox comboBox && !Equals(comboBox.SelectedItem, initialValue)))\n            {\n                _isDirty = true;\n            }\n        }\n    }\n\n    private void MyForm_Load(object sender, EventArgs e)\n    {\n        // Attach event handlers after form load if data binding happens here\n        textBox1.TextChanged += Control_TextChangedOrSelectedIndexChanged;\n        comboBox1.SelectedIndexChanged += Control_TextChangedOrSelectedIndexChanged;\n        // Add more controls as necessary\n    }\n}