using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing Microsoft.VisualBasic.FileIO;\n\nnamespace ConsoleApp\n{\n    class Program\n    {\n        static void Main()\n        {\n            var fullPath = @"C:\Users\Owner\Desktop\IR\IR_Files\Absolute\MyTextFile.CSV";\n            \n            using (TextFieldParser parser = new TextFieldParser(fullPath))\n            {\n                parser.TextFieldType = FieldType.Delimited;\n                parser.SetDelimiters("|");\n                \n                while (!parser.EndOfData)\n                {\n                    string[] fields = parser.ReadFields();\n                    \n                    if (fields != null)\n                    {\n                        // Access the fields by index\n                        Console.WriteLine($"Full Stock Number: {fields[0]}, Full Info: {fields[1]}");\n                    }\n                }\n            }\n        }\n    }\n}